        { icon: 'üì∫', label: 'Watch' },
        { icon: 'üéâ', label: 'Events' },
        { icon: 'üìÖ', label: 'Memories' },
        { icon: 'üíæ', label: 'Saved' },
        { icon: 'üë•', label: 'Groups' },
        { icon: 'üè™', label: 'Marketplace' },
        { icon: 'üì∞', label: 'News' },
    ];

    return (
        <div className="left-sidebar">
            {menuItems.map((item, index) => (
                <div key={index} className="sidebar__item">
                    <span className="sidebar__icon">{item.icon}</span>
                    <span className="sidebar__label">{item.label}</span>
                </div>
            ))}
            <div className="sidebar__item">
                <span className="sidebar__icon">‚¨áÔ∏è</span>
                <span className="sidebar__label">See More</span>
            </div>
            <hr className="sidebar__divider" />
            <h4 className="sidebar__heading">Your Shortcuts</h4>
            <div className="sidebar__shortcuts">
                <div className="sidebar__item">
                    <span className="sidebar__icon">üéÆ</span>
                    <span className="sidebar__label">Gaming Group</span>
                </div>
                <div className="sidebar__item">
                    <span className="sidebar__icon">üíª</span>
                    <span className="sidebar__label">React Developers</span>
                </div>
            </div>
        </div>
    );
};

export default LeftSidebar;
